import React, { Component } from 'react';
import { Progress } from 'react-sweet-progress';
import "react-sweet-progress/lib/style.css";
import ListOfProjs from './ListOfProjs';
import ListOfDoneProjs from './ListOfDoneProjs';

class Project extends Component {
	
	constructor(props){
		super(props);
		this.state = {
			projs: [],
			percentage: 10,
			status: 'error'
		}
	}
	
	doneElement = (key) => {
		
		let tempProjs = [];
		
		for(let i=0;i<this.state.projs.length;i++)
		{
			let x = this.state.projs[i];
			
			if(x.proj_key===key)
			{
				x.proj_done = 1;
			}
			
			tempProjs.push(x);
		}
		
		//console.log(this.state.projs);
		
		this.setState({projs: tempProjs});
		this.props.loadUserproj(tempProjs);
		
		//console.log(tempProjs);
	}
	
	undoElement = (key) => {
		
		let tempProjs = [];
		
		for(let i=0;i<this.state.projs.length;i++)
		{
			let x = this.state.projs[i];
			
			if(x.proj_key===key)
			{
				x.proj_done = 0;
			}
			
			tempProjs.push(x);
		}
		
		//console.log(this.state.projs);
		
		this.setState({projs: tempProjs});
		this.props.loadUserproj(tempProjs);
		
		//console.log(tempProjs);
	}
	
	delElement = (key) => {
		
		let tempProjs = [];
		
		for(let i=0;i<this.state.projs.length;i++)
		{
			let x = this.state.projs[i];
			
			if(x.proj_key!==key)
			{
				tempProjs.push(x);
			}
		}
		
		//console.log(this.state.projs);
		
		this.setState({projs: tempProjs});
		this.props.loadUserproj(tempProjs);
		
		//console.log(tempProjs);
	}
	
	addElement = (eve) => {
		if(this._inputElement.value !== "")
		{
			let newItem = {
				proj_name: "proj1",
				proj_task: this._inputElement.value,
				proj_done: 0,
				proj_key: Date.now()
			};
			
			let projTemp = this.state.projs;
			projTemp.push(newItem);
			this.setState({projs: projTemp});
			this.props.loadUserproj(projTemp);
			
			this._inputElement.value = "";
		}
		
		eve.preventDefault();
	}
	
	componentDidMount() {
		this.setState({projs: this.props.projvals});
		
		let n, d;
		
		for(let i=0;i<this.props.projvals.length;i++)
		{
			let x = this.props.projvals[i];
			
			if(x.proj_done==0)
				d++;
			else
				n++;
		}
		
		if(d==0)
		{
			this.setState({percentage: 10});
			this.setState({status: 'error'})
		}
		else
		{
			this.setState({percentage: (n/d)});
			if(((n*100)/d)<=25)
				this.setState({status: 'error'})
			else if(((n*100)/d)<=60)
				this.setState({status: 'active'})
			else
				this.setState({status: 'success'})
		}
		
	}
	
	render() {
		let {percentage, status} = this.state;
		return(
			<div className='proj'>
				<form className='projAdd' onSubmit={this.addElement}>
					<input type="text" placeholder="" className="inp mt2" ref={(a) => {this._inputElement = a}}/>
					<button className='add dib bw0 grow shadow-5' type='submit'>add</button>
				</form>
				<ListOfProjs projs={this.state.projs} doneElement={this.doneElement}/>
				<Progress percent={percentage} status={ `${this.state.status}` } />
				<ListOfDoneProjs projs={this.state.projs} undoElement={this.undoElement} delElement={this.delElement}/>
			</div>
			);
	}
}

export default Project;
