import React, { Component } from 'react';

class ListOfProjs extends Component {
	
	constructor(props) {
		super(props);
		
		this.createTask = this.createTask.bind(this);
	}
	
	doneTask(key) {
		this.props.doneElement(key);
	}
	
	createTask(proj) {
		if(proj.proj_done===0)
		{
			return <li onClick={() => {this.doneTask(proj.proj_key)}} key={proj.proj_key}>{proj.proj_task}</li>
		}
	}
	
	render() {
		let list = this.props.projs.map((proj) => {return(this.createTask(proj))});
		
		return(
			<ul>
				{list}
			</ul>
		);
	}
}

export default ListOfProjs;
