import React, { Component } from 'react';
import ListOfProjs from './ListOfProjs';

class Project extends Component {
	
	constructor(props){
		super(props);
		this.state = {
			projs: []
		}
	}
	
	doneElement = (name, task) => {
		let leftProjs = this.state.projs.filter(
						(ele) => {return((ele.proj_name===name) && (ele.proj_task!==task))}
						);
		
		this.setState({projs: leftProjs});
		this.props.loadUserproj(leftProjs);
	}
	
	addElement = (eve) => {
		if(this._inputElement.value !== "")
		{
			let newItem = {
				proj_name: "proj1",
				proj_task: this._inputElement.value
			};
			
			let projTemp = this.state.projs;
			projTemp.push(newItem);
			this.setState({projs: projTemp});
			this.props.loadUserproj(projTemp);
			
			this._inputElement.value = "";
		}
		
		eve.preventDefault();
	}
	
	componentDidMount() {
		this.setState({projs: this.props.projvals});
	}
	
	render() {
		return(
			<div className='proj'>
				<form className='projAdd' onSubmit={this.addElement}>
					<input type="text" placeholder="" className="inp mt2" ref={(a) => {this._inputElement = a}}/>
					<button className='add dib bw0 grow shadow-5' type='submit'>add</button>
				</form>
				<ListOfProjs projs={this.state.projs} doneElement={this.doneElement}/>
			</div>
			);
	}
}

export default Project;
