import React, { Component } from 'react';
import ListOfJobs from './ListOfProjs';
import './Project.css';

class Project extends Component {
	
	constructor(props){
		super(props);
		this.state = {
			projs: []
		}
		
		this.addElement = this.addElement.bind(this);
		this.delElement = this.delElement.bind(this);
	}
	
	delElement = (key) => {
		//console.log(key);
		let leftProjs = this.state.projs.filter((ele) => {
			return(ele.key !== key)});
		
		//console.log(leftJobs);
		
		this.setState({projs: leftProjs});
		this.props.loadUserproj(leftProjs);
	}
	
	addElement = (eve) => {
		
		// console.log('inp val', this._inputElement.value);
		// console.log('projs state prev', this.state.projs);
		
		if(this._inputElement.value !== "") {
			let newItem = {
				text: this._inputElement.value,
				key: Date.now()
			};
			
			// this.setState((prevState) => {
				
			// 	console.log(prevState.projs);
				
			// 	return {
			// 		projs: prevState.projs.push(newItem)
			// 	};
			// });
			
			let projsTemp = this.state.projs;
			projsTemp.push(newItem);
			this.setState({projs: projsTemp});
			this.props.loadUserproj(projsTemp);
			
			this._inputElement.value = "";
		}
		
		// console.log('projs state new', this.state.projs);
		
		eve.preventDefault();
	}
	
	componentDidMount() {
		//console.log(this.props.todovals)
		this.setState({projs: this.props.projs});
		
		// axios.get(req,res=>
		// 	{this.set})
	}
	
	render() {
		 console.log('inside render', this.state.projs)
		return(
			<div className='todo1'>
				<form className='jobAdd' onSubmit={this.addElement}>
					<input type="text" placeholder="" className="inp mt2" ref={(a) => {this._inputElement = a}}/>
					<button className='add dib bw0 grow shadow-5' type='submit'>add</button>
				</form>
				<div>
					<ListOfProjs className='jobsList' projs={this.state.projs} delElement={this.delElement}/>
				</div>
				<div className='float dib bw2 grow shadow-5' onClick={() => {this.props.onRouteChange('timeTable')}}>
					<p className='my-float'>Time-Table</p>
				</div>
				<div className='float dib bw2 grow shadow-5' onClick={() => {this.props.onRouteChange('todo')}}>
					<p className='my-float'>Todo</p>
				</div>
			</div>
		);
	}
}

export default Project;
