import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import Landing from './components/Landing/Landing';
import Timetable from './components/Timetable/Timetable';
import Todo from './components/Todo/Todo';
import Project from './components/Project/Project';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.state= {
      isSignedIn: false,
      route: 'landing',
      user: {
        name:'',
        email:'',
        timeTable: [
          ['','','','','','','','',''],
          ['','','','','','','','',''],
          ['','','','','','','','',''],
          ['','','','','','','','',''],
          ['','','','','','','','',''],
          ['','','','','','','','',''],
          ['','','','','','','','','']
        ],
        todo: [],
        project: []
      }
    }

    //this.onDataSave = this.onDataSave.bind(this);
  }

  loadUsertt = (allDays) => {
    let userTemp = this.state.user;
    userTemp.timeTable = allDays;
    this.setState({user: userTemp});
    // console.log(this.state.user.timeTable);
  }

  loadUsertodo = (allJobs) => {
    let userTemp = this.state.user;
    userTemp.todo = allJobs;
    this.setState({user: userTemp});
    //console.log('inside loadUsertodo', this.state.user.todo);
    //console.log('inside loadUsertodo', allJobs);
    //console.log(this.state.user.todo)
    //console.log(this.state.user)
  }

  loadUserproj = (allProjs) => {
    let userTemp = this.state.user;
    userTemp.project = allProjs;
    this.setState({user: userTemp});
    //console.log('inside loadUsertodo', this.state.user.todo);
    //console.log('inside loadUsertodo', allJobs);
    //console.log(this.state.user.project)
  }

  loadUser = (user) => {
    //console.log(user);
    this.setState({user: user});
    //console.log(this.state.user);
  }

  // onDataSave = () => {}


  onRouteChange = (route) => {

    if(route==='landing')
    {
      this.setState({isSignedIn: false});
    }
    else if(route==='timeTable' || route==='todo' || route==='project')
      this.setState({isSignedIn: true});

    if((this.state.route==='todo' || this.state.route==='timeTable' || this.state.route==='project') && route==='landing')
    {
      //console.log(this.state.user);
      fetch('http://localhost:3001/signout', {
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
          name: this.state.user.name,
          email: this.state.user.email,
          timeTable: this.state.user.timeTable,
          todo: this.state.user.todo,
          project: this.state.user.project
        })
      })
      .then(response => {
        response.json()
      })
      .then((user) => {
       //console.log(user);
          // if(user==='success')
          //   this.setState({route: route});
          // else
          //   console.log('error logging out');
      })
      .catch((err) => {console.log(err)})
    }

    this.setState({route: route});
  }

  render() {

    const { route, isSignedIn } = this.state;

    return (
      <div className="App">
        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange} />
        {
          (route==='signin')?
            <Signin onRouteChange={this.onRouteChange} loadUser={this.loadUser} />
            :
            (route==='register')?
              <Register onRouteChange={this.onRouteChange} loadUser={this.loadUser} />
              :
              (route==='landing')?
                <Landing />
                :
                (route==='timeTable')?
                  <Timetable loadUsertt={this.loadUsertt} ttvals={this.state.user.timeTable} onRouteChange={this.onRouteChange} />
                  :
                  (route==='todo')?
                  <Todo loadUsertodo={this.loadUsertodo} todovals={this.state.user.todo} onRouteChange={this.onRouteChange} />
                  :
                  <Project loadUserproj={this.loadUserproj} projvals={this.state.user.project} onRouteChange={this.onRouteChange} />
        }
      </div>
    );
  }
}

export default App;
